import { Component, inject } from "@angular/core";
import { AbstractControl, FormBuilder, Validators } from "@angular/forms";
import { MatSnackBar } from "@angular/material/snack-bar";
import { AuthComponent } from "src/app/pages/auth/auth.component";
import { ApiAuthService } from "src/app/services/apiAuthService";
import validations from '../../helpers/authValidations'
import { HttpErrorResponse } from "@angular/common/http";

function samePass(control: AbstractControl) {
    const PASS = control.get('password');
    const CONFIRM_PASS = control.get('confirmPassword');
    
    if (PASS?.value !== CONFIRM_PASS?.value) {
        CONFIRM_PASS?.setErrors({...CONFIRM_PASS.errors, passNotMatch: true})
    } else {
        CONFIRM_PASS?.setErrors(null)
    }
    return null
}

@Component({
    selector: "sign-up-form",
    templateUrl: './signUpForm.component.html',
    styleUrls: ['./signUpForm.component.scss']
})
export class SignUpFormComponent {
    formBuilder = inject(FormBuilder);
    authService = inject(ApiAuthService);
    authPage = inject(AuthComponent);
    private _snackbar = inject(MatSnackBar)

    signUpForm = this.formBuilder.group({
        email: [
            "",
            [
                Validators.required,
                Validators.email
            ]
        ],
        name: [
            "",
            [
                Validators.required,
                Validators.minLength(4)
            ]
        ],
        password: [
            "",
            [
                Validators.required,
                Validators.pattern(/^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$/)
            ]
        ],
        confirmPassword: '',
        picture: [
            "data:image/png;base64,",
            Validators.pattern("")
        ]
    }, {
        validators: samePass
    }
    )

    emailError = () => validations.getEmailError(this.signUpForm.controls['email'])

    nameError = () => validations.getNameError(this.signUpForm.controls['name']);

    pwdError = () => validations.getPasswordError(this.signUpForm.controls['password']);

    confirmPwdError = () => validations.getConfirmPasswordError(this.signUpForm.controls['confirmPassword']);

    signUp() {
        this.authService.signUp(this.signUpForm.getRawValue()).subscribe({
            next: response => {
                if(response.success === 1) {
                    this.authPage.changeForm();
                }
            },
            error: (error: HttpErrorResponse) => {
                const label = 'Okey'
                const config = {
                    duration: 500
                }
                if (error.status === 400) this._snackbar.open('Missing information.', label, config)
                else this._snackbar.open('Server error, please try again later.', label, config)
            }
        })
    }
}